/***************************************套模板部分方法**************************************************/
        public  boolean  CreatePDF(String [] str,String filename,String TemplateFilename,String [] PublicStr)
        {
            boolean yn=true;
            try{
                PdfReader reader=new PdfReader(TemplateFilename);
                PdfStamper stamp=new PdfStamper(reader,new FileOutputStream(filename));
                PdfContentByte under=stamp.getUnderContent(1);
                BaseFont bf = BaseFont.createFont("STSong-Light", "UniGB-UCS2-H",
                                      BaseFont.NOT_EMBEDDED);
               Font FontChinese = new Font(bf, 12, Font.NORMAL);
               AcroFields form=stamp.getAcroFields();

               for (int i=0;i<str.length;i++)
               {
                   if (str[i]==null)
                   {
                       str[i]="";
                       Stringtools.log("","写文件时数组值情况====>str["+i+"] is null","");
                   }
                   Stringtools.log("","str["+i+"] "+str[i],"");
                   form.setField("Text"+(i+1),str[i]);

               }
               for (int i=0;i<PublicStr.length;i++)
               {
                   Stringtools.log("","=============正在写公共数据！！======"+PublicStr[i],"");
                   logger.debug("=============正在写公共数据！！======"+PublicStr[i]);
                   form.setField("Public"+(i+1),PublicStr[i]);
               }
               stamp.setFormFlattening(true);
               stamp.close();
           }catch(DocumentException de)
           {
               yn=false;
               Stringtools.log("","套用模板时出错："+"MU_Template.java"+de.getMessage(),"");
           }catch(IOException ioe)
           {
               yn=false;
               Stringtools.log("","套用模板时出错:"+"MU_Template.java"+ioe.getMessage(),"");
           }

           return yn;
       }


 /*****************************************判断页数等*****************************************************/
        public static boolean createreport(String [] str,String filename,String TemplateFilename,int num,String [] PublicStr)
        {
            boolean yn=true;
            String page="";
                   //计算一下一共有多少页
                   int totalpages=0;
                   //下传文件名
                   String filename1="";
                   if ((double) str.length/ (double) num==(double) (str.length/num))
                   {
                       totalpages=str.length/num;
                   }
                   else
                   {
                       totalpages=(int)(str.length/num)+1;
                   }
                   Stringtools.log("","str.length:"+str.length,"");
                   Stringtools.log("","总页数:"+totalpages,"");

                   MU_Template mypdf=new MU_Template();
                   for (int i=1;i<=totalpages;i++)
                   {
                       String [] str_tmp=new String [num];
                       filename1=filename.substring(0,filename.indexOf("."))+i+".pdf";
                       Stringtools.log("","生成文件时:"+filename1+"  num:"+num+"  I:"+i,"");
                       for (int j=0;j<num;j++)
                       {
                           Stringtools.log("","i:"+i+" j:"+j,"");
                           if ((j+(i-1)*num)>=str.length)
                           {
                               break;
                           }
                           else
                           {
                               str_tmp[j]=str[j+((i-1)*num)];
                           }
                       }

                       // page="page  "+i+ "  of   "+totalpages;
                       PublicStr[3]="第 "+i+" 页 ,共 "+totalpages+" 页";
                       //将数据传入写模板方法
                       if (!mypdf.CreatePDF(str_tmp, filename1, TemplateFilename,PublicStr))
                       {
                           return false;
                       }
                   }
                   //开始文件相加
                   try {
                       //生成新的文件
                       PdfCopyFields copy = new PdfCopyFields(new FileOutputStream(filename));

                       //几个文件相加

                       for (int k=1;k<=totalpages;k++)
                       {
                       Stringtools.log("","文件相加："+filename.substring(0,filename.indexOf("."))+k+".pdf","");
                       PdfReader reader1= new PdfReader(filename.substring(0,filename.indexOf("."))+k+".pdf");
                       copy.addDocument(reader1);
                       }
                        Stringtools.log("","生成新文件："+filename,"");
                        copy.close();
                        //删除用过的文件
                        for (int k=1;k<=totalpages;k++)
                       {
                         java.io.File myfile=new java.io.File(filename.substring(0,filename.indexOf("."))+k+".pdf");

                           if (myfile.exists())
                           {
                               myfile.delete();
                               Stringtools.log("","删除临时文件："+filename.substring(0,filename.indexOf("."))+k+".pdf","");
                           }
                       }


                } catch (Exception e) {
                        e.printStackTrace();
                        Stringtools.log("","文件copy时出错:(file copy)"+e.getMessage(),"");
                        yn=false;
                }
            return yn;
        }
        public static String gettime()
       {
                 java.util.Calendar cal=java.util.Calendar.getInstance();
                 String  hour="00"+cal.get(cal.HOUR);
                 String  minute="00"+cal.get(cal.MINUTE);
                 String  second="00"+cal.get(cal.SECOND);
                 String strtime=hour.substring(hour.length()-2,hour.length())+minute.substring(minute.length()-2,minute.length())+second.substring(second.length()-2,second.length());
                 return  strtime;

        }


